// Test data configuration for Mastodon workflow
// Many ID fields below are intentionally empty and will cause specific tests to fail.
// Downstream nodes use continueOnFail: true to tolerate these expected failures.
// To enable full testing, populate these IDs with real values from your Mastodon instance:

// Runtime base URL with env fallback
let baseUrl = 'https://mastodon.social';
try {
  const envUrl = $env.MASTODON_TEST_URL;
  if (typeof envUrl === 'string' && envUrl.trim()) baseUrl = envUrl.trim();
} catch (_error) {}

return [{ json: {
  baseUrl,
  // Optional explicit target for pin/unpin tests (leave empty to skip safely)
  pinAccountId: '',
  searchQuery: 'mastodon',
  hashtag: 'n8n',
  externalUrl: 'https://n8n.io',
  testDomain: 'example.invalid',
  testEmailDomain: 'mail.example.invalid',
  testTag: 'n8n',

  // Account-related IDs (will fail: Follow Requests Authorize/Reject, Pin/Block/Mute Account ops)
  pendingFollowAccountId: '',  // Required for: 57 Follow Requests Authorize, 58 Follow Requests Reject
  followerAccountId: '',        // Required for: 10 Pin Account, 34 Block Account, 36 Mute Account

  // Notification/Conversation IDs (will fail: Notification Get/Dismiss, Conversation Read/Remove)
  notificationId: '',           // Required for: 52 Notifications Dismiss
  conversationId: '',           // Required for: 54 Conversations Mark Read, 55 Conversations Remove

  // Admin domain IDs (will fail: domain query operations)
  allowedDomainId: '',          // Required for: 71 Allowed Domains Get
  blockedDomainId: '',          // Required for: 73 Blocked Domains Get
  emailBlockedDomainId: '',     // Required for: 76 Email Blocked Domains Get
  canonicalEmailBlockId: '',    // Required for: 97 Canonical Email Blocks Get

  // Admin analytics IDs (will fail: dimension, IP, measure queries)
  dimensionId: '',              // Required for: 93 Dimensions Get
  ipRecordId: '',               // Required for: 95 IPs Get
  measureId: '',                // Required for: 89 Measures Get Metrics

  // Media/Poll/Status IDs (will fail: media update, poll view/vote, scheduled status operations)
  mediaId: '',                  // Required for: 107 Media Update
  pollId: '',                   // Required for: 105 Poll View, 106 Poll Vote
  scheduledStatusId: '',        // Required for: 99 Scheduled Statuses View, 100 Update, 101 Cancel

  // OAuth credential IDs (will fail: authentication operations)
  oauthClientId: '',            // Required for: OAuth-based authentication operations
  oauthClientSecret: '',        // Required for: OAuth-based authentication operations
  oauthCode: '',                // Required for: OAuth token exchange
  oauthTokenToRevoke: '',       // Required for: OAuth token revocation

  // Push subscription config (placeholder values - real endpoint needed for actual testing)
  pushEndpoint: 'https://example.invalid/push',
  pushP256dh: 'BMkFakeP256dhKeyForTestingOnly',
  pushAuth: 'FakeAuthSecretForTestingOnly'
}}];
